"use client"

import { useEffect, useState } from "react"
import SortableTable from "../../components/SortableTable"
import api from "../../services/api"

const UserDashboard = () => {
  const [stores, setStores] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [successMessage, setSuccessMessage] = useState("")
  const [filters, setFilters] = useState({
    name: "",
    address: "",
  })
  const [showRatingModal, setShowRatingModal] = useState(false)
  const [selectedStore, setSelectedStore] = useState(null)
  const [rating, setRating] = useState(0)
  const [ratingLoading, setRatingLoading] = useState(false)

  useEffect(() => {
    fetchStores()
  }, [])

  const fetchStores = async () => {
    try {
      setLoading(true)
      const response = await api.get("/user/stores", { 
        params: filters,
        headers: {
          'Cache-Control': 'no-cache'
        }
      })

      // Transform data to ensure proper types
      const validatedData = response.data.map(store => ({
        ...store,
        rating: store.rating ? parseFloat(store.rating) : null,
        userRating: store.userRating ? parseInt(store.userRating) : null
      }))

      setStores(validatedData)
      setError("")
    } catch (err) {
      setError("Failed to load stores. Please try again.")
      console.error("Fetch stores error:", err)
    } finally {
      setLoading(false)
    }
  }

  const handleFilterChange = (e) => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  const applyFilters = (e) => {
    e.preventDefault()
    fetchStores()
  }

  const resetFilters = () => {
    setFilters({
      name: "",
      address: "",
    })
  }

  const openRatingModal = (store) => {
    setSelectedStore(store)
    setRating(store.userRating || 0)
    setShowRatingModal(true)
    setError("")
    setSuccessMessage("")
  }

  const submitRating = async (e) => {
    e.preventDefault()

    // Validate rating
    if (rating < 1 || rating > 5) {
      setError("Rating must be between 1 and 5")
      return
    }

    setRatingLoading(true)

    try {
      // Optimistic update
      const updatedStores = stores.map(store => 
        store.id === selectedStore.id 
          ? { 
              ...store, 
              userRating: rating,
              rating: calculateNewAverage(store.rating, rating, store.userRating, store.ratingCount)
            } 
          : store
      )
      setStores(updatedStores)

      // Submit to server
      const response = await api.post(
        `/user/stores/${selectedStore.id}/rate`, 
        { rating },
        {
          headers: {
            'Cache-Control': 'no-cache'
          }
        }
      )

      // Update with server data
      setStores(prev => 
        prev.map(store => 
          store.id === response.data.store.id 
            ? { 
                ...store, 
                rating: response.data.store.rating,
                userRating: response.data.store.user_rating
              } 
            : store
        )
      )

      setSuccessMessage(response.data.message)
      setTimeout(() => setSuccessMessage(""), 3000)
      setShowRatingModal(false)
    } catch (err) {
      // Revert optimistic update on error
      setStores(prev => 
        prev.map(store => 
          store.id === selectedStore.id ? selectedStore : store
        )
      )

      const errorMsg = err.response?.data?.message || 
                      err.response?.data?.error ||
                      err.message || 
                      "Failed to submit rating"
      setError(errorMsg)
      console.error("Rating submission error:", err)
    } finally {
      setRatingLoading(false)
    }
  }

  // Helper function to calculate new average
  const calculateNewAverage = (currentAvg, newRating, oldRating, ratingCount) => {
    if (!oldRating) {
      // New rating
      const total = (currentAvg * ratingCount) + newRating
      return total / (ratingCount + 1)
    } else {
      // Updated rating
      const total = (currentAvg * ratingCount) - oldRating + newRating
      return total / ratingCount
    }
  }

  const columns = [
    { key: "name", label: "Store Name", sortable: true },
    { key: "address", label: "Address", sortable: true },
    {
      key: "rating",
      label: "Overall Rating",
      sortable: true,
      render: (row) => (
        <span className="overall-rating">
          {row.rating ? `${row.rating.toFixed(1)} ★` : "No ratings"}
        </span>
      ),
    },
    {
      key: "userRating",
      label: "Your Rating",
      sortable: true,
      render: (row) => (
        <span className={`user-rating ${row.userRating ? "rated" : "not-rated"}`}>
          {row.userRating ? `${row.userRating} ★` : "Not rated"}
        </span>
      ),
    },
    {
      key: "actions",
      label: "Actions",
      sortable: false,
      render: (row) => (
        <button
          className="rate-button"
          onClick={(e) => {
            e.stopPropagation()
            openRatingModal(row)
          }}
          disabled={ratingLoading}
        >
          {row.userRating ? "Update Rating" : "Rate Store"}
        </button>
      ),
    },
  ]

  return (
    <div className="user-dashboard">
      <h1>Stores</h1>

      {/* Status messages */}
      {successMessage && (
        <div className="alert success">
          {successMessage}
        </div>
      )}
      {error && (
        <div className="alert error">
          {error}
        </div>
      )}

      {/* Filters */}
      <div className="filters">
        <h3>Search Stores</h3>
        <form onSubmit={applyFilters}>
          <div className="filter-grid">
            <div className="form-group">
              <label htmlFor="name">Store Name</label>
              <input 
                type="text" 
                id="name" 
                name="name" 
                value={filters.name} 
                onChange={handleFilterChange} 
                placeholder="Search by name..."
              />
            </div>

            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input 
                type="text" 
                id="address" 
                name="address" 
                value={filters.address} 
                onChange={handleFilterChange} 
                placeholder="Search by address..."
              />
            </div>
          </div>

          <div className="filter-actions">
            <button type="submit" className="btn primary">
              Search
            </button>
            <button 
              type="button" 
              className="btn secondary" 
              onClick={resetFilters}
            >
              Reset
            </button>
          </div>
        </form>
      </div>

      {/* Stores table */}
      {loading ? (
        <div className="loading-state">
          <div className="spinner"></div>
          <p>Loading stores...</p>
        </div>
      ) : (
        <SortableTable 
          columns={columns} 
          data={stores} 
          emptyMessage="No stores found matching your criteria"
        />
      )}

      {/* Rating modal */}
      {showRatingModal && selectedStore && (
        <div className="modal-backdrop">
          <div className="modal">
            <div className="modal-header">
              <h2>
                {selectedStore.userRating ? "Update Your Rating" : "Rate This Store"}
              </h2>
              <button 
                className="close-button" 
                onClick={() => setShowRatingModal(false)}
                disabled={ratingLoading}
              >
                &times;
              </button>
            </div>

            <form onSubmit={submitRating}>
              <div className="modal-body">
                <div className="store-info">
                  <h3>{selectedStore.name}</h3>
                  <p>{selectedStore.address}</p>
                </div>

                <div className="rating-input">
                  <label>Your Rating:</label>
                  <div className="stars">
                    {[1, 2, 3, 4, 5].map((star) => (
                      <button
                        key={star}
                        type="button"
                        className={`star ${star <= rating ? "active" : ""}`}
                        onClick={() => setRating(star)}
                        disabled={ratingLoading}
                      >
                        ★
                      </button>
                    ))}
                  </div>
                  <div className="rating-value">
                    Selected: {rating} star{rating !== 1 ? 's' : ''}
                  </div>
                </div>
              </div>

              <div className="modal-footer">
                <button
                  type="button"
                  className="btn secondary"
                  onClick={() => setShowRatingModal(false)}
                  disabled={ratingLoading}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="btn primary"
                  disabled={ratingLoading}
                >
                  {ratingLoading ? (
                    <>
                      <span className="spinner"></span>
                      Processing...
                    </>
                  ) : (
                    "Submit Rating"
                  )}
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

