"use client"

import { useState, useEffect } from "react"
import api from "../../services/api"
import SortableTable from "../../components/SortableTable"

const UserDashboard = () => {
  const [stores, setStores] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [filters, setFilters] = useState({
    name: "",
    address: "",
  })
  const [showRatingModal, setShowRatingModal] = useState(false)
  const [selectedStore, setSelectedStore] = useState(null)
  const [rating, setRating] = useState(0)

  useEffect(() => {
    fetchStores()
  }, [])

  const fetchStores = async () => {
    try {
      setLoading(true)
      const response = await api.get("/user/stores", { params: filters })
      setStores(response.data)
      setError("")
    } catch (err) {
      setError("Failed to load stores")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  const handleFilterChange = (e) => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  const applyFilters = (e) => {
    e.preventDefault()
    fetchStores()
  }

  const resetFilters = () => {
    setFilters({
      name: "",
      address: "",
    })
  }

  const openRatingModal = (store) => {
    setSelectedStore(store)
    setRating(store.userRating || 0)
    setShowRatingModal(true)
  }

  const submitRating = async (e) => {
    e.preventDefault()
    try {
      await api.post(`/user/stores/${selectedStore.id}/rate`, { rating })
      setShowRatingModal(false)
      fetchStores()
    } catch (err) {
      setError(err.response?.data?.message || "Failed to submit rating")
    }
  }

  const columns = [
    { key: "name", label: "Store Name", sortable: true },
    { key: "address", label: "Address", sortable: true },
    {
      key: "rating",
      label: "Overall Rating",
      sortable: true,
      render: (row) => (row.rating ? row.rating.toFixed(1) : "No ratings"),
    },
    {
      key: "userRating",
      label: "Your Rating",
      sortable: true,
      render: (row) => (row.userRating ? row.userRating : "Not rated"),
    },
    {
      key: "actions",
      label: "Actions",
      sortable: false,
      render: (row) => (
        <button
          className="btn btn-primary btn-sm"
          onClick={(e) => {
            e.stopPropagation()
            openRatingModal(row)
          }}
        >
          {row.userRating ? "Update Rating" : "Rate Store"}
        </button>
      ),
    },
  ]

  return (
    <div className="container">
      <h1>Stores</h1>
      {error && <div className="error-message">{error}</div>}

      <div className="filters-container">
        <h3>Search Stores</h3>
        <form onSubmit={applyFilters}>
          <div className="filters-grid">
            <div className="form-group">
              <label htmlFor="name">Store Name</label>
              <input type="text" id="name" name="name" value={filters.name} onChange={handleFilterChange} />
            </div>

            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" id="address" name="address" value={filters.address} onChange={handleFilterChange} />
            </div>
          </div>

          <div className="filters-actions">
            <button type="submit" className="btn btn-primary">
              Search
            </button>
            <button type="button" className="btn btn-secondary" onClick={resetFilters}>
              Reset
            </button>
          </div>
        </form>
      </div>

      {loading ? <div className="loading">Loading stores...</div> : <SortableTable columns={columns} data={stores} />}

      {showRatingModal && selectedStore && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>{selectedStore.userRating ? "Update Rating" : "Rate Store"}</h2>
              <button className="modal-close" onClick={() => setShowRatingModal(false)}>
                &times;
              </button>
            </div>

            <form onSubmit={submitRating}>
              <div className="form-group">
                <label>Store: {selectedStore.name}</label>
              </div>

              <div className="form-group">
                <label htmlFor="rating">Your Rating (1-5)</label>
                <div className="rating-input">
                  {[1, 2, 3, 4, 5].map((value) => (
                    <button
                      key={value}
                      type="button"
                      className={`rating-star ${value <= rating ? "active" : ""}`}
                      onClick={() => setRating(value)}
                    >
                      ★
                    </button>
                  ))}
                </div>
              </div>

              <div className="modal-actions">
                <button type="submit" className="btn btn-primary">
                  Submit Rating
                </button>
                <button type="button" className="btn btn-secondary" onClick={() => setShowRatingModal(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  )
}

export default UserDashboard
