"use client"

import { useEffect, useState } from "react"
import SortableTable from "../../components/SortableTable"
import api from "../../services/api"

const UserDashboard = () => {
  const [stores, setStores] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [filters, setFilters] = useState({
    name: "",
    address: "",
  })
  const [showRatingModal, setShowRatingModal] = useState(false)
  const [selectedStore, setSelectedStore] = useState(null)
  const [rating, setRating] = useState(0)
  const [ratingLoading, setRatingLoading] = useState(false)
  const [successMessage, setSuccessMessage] = useState("")

  useEffect(() => {
    fetchStores()
  }, [])

  const fetchStores = async () => {
    try {
      setLoading(true)
      const response = await api.get("/user/stores", { params: filters })
      setStores(response.data)
      setError("")
    } catch (err) {
      setError("Failed to load stores")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  const handleFilterChange = (e) => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  const applyFilters = (e) => {
    e.preventDefault()
    fetchStores()
  }

  const resetFilters = () => {
    setFilters({
      name: "",
      address: "",
    })
  }

  const openRatingModal = (store) => {
    setSelectedStore(store)
    setRating(store.userRating || 0)
    setShowRatingModal(true)
    setError("")
    setSuccessMessage("")
  }

  const submitRating = async (e) => {
    e.preventDefault()
    setRatingLoading(true)

    try {
      // Optimistic UI update
      setStores(prevStores => 
        prevStores.map(store => 
          store.id === selectedStore.id 
            ? { ...store, userRating: rating } 
            : store
        )
      )

      await api.post(`/user/stores/${selectedStore.id}/rate`, { rating })

      setSuccessMessage("Rating submitted successfully!")
      setTimeout(() => setSuccessMessage(""), 3000)
      setShowRatingModal(false)

      // Optional: Refresh data from server to ensure consistency
      // await fetchStores()
    } catch (err) {
      // Revert optimistic update on error
      setStores(prevStores => 
        prevStores.map(store => 
          store.id === selectedStore.id 
            ? { ...store, userRating: selectedStore.userRating } 
            : store
        )
      )

      const errorMessage = err.response?.data?.message || 
                         err.message || 
                         "Failed to submit rating"
      setError(errorMessage)
      console.error("Rating submission error:", err)
    } finally {
      setRatingLoading(false)
    }
  }

  const columns = [
    { key: "name", label: "Store Name", sortable: true },
    { key: "address", label: "Address", sortable: true },
    {
      key: "rating",
      label: "Overall Rating",
      sortable: true,
      render: (row) =>
        typeof row.rating === "number" ? row.rating.toFixed(1) : "No ratings",
    },
    {
      key: "userRating",
      label: "Your Rating",
      sortable: true,
      render: (row) => (
        <span className={row.userRating ? "user-rating" : "no-rating"}>
          {row.userRating ? `${row.userRating} ★` : "Not rated"}
        </span>
      ),
    },
    {
      key: "actions",
      label: "Actions",
      sortable: false,
      render: (row) => (
        <button
          className="btn btn-primary btn-sm"
          onClick={(e) => {
            e.stopPropagation()
            openRatingModal(row)
          }}
        >
          {row.userRating ? "Update Rating" : "Rate Store"}
        </button>
      ),
    },
  ]

  return (
    <div className="container">
      <h1>Stores</h1>

      {/* Success and error messages */}
      {successMessage && (
        <div className="alert alert-success">
          {successMessage}
        </div>
      )}
      {error && (
        <div className="alert alert-danger">
          {error}
        </div>
      )}

      <div className="filters-container card mb-4">
        <div className="card-body">
          <h3 className="card-title">Search Stores</h3>
          <form onSubmit={applyFilters}>
            <div className="row">
              <div className="col-md-6 mb-3">
                <label htmlFor="name" className="form-label">Store Name</label>
                <input 
                  type="text" 
                  id="name" 
                  name="name" 
                  className="form-control"
                  value={filters.name} 
                  onChange={handleFilterChange} 
                />
              </div>

              <div className="col-md-6 mb-3">
                <label htmlFor="address" className="form-label">Address</label>
                <input 
                  type="text" 
                  id="address" 
                  name="address" 
                  className="form-control"
                  value={filters.address} 
                  onChange={handleFilterChange} 
                />
              </div>
            </div>

            <div className="d-flex gap-2">
              <button type="submit" className="btn btn-primary">
                Search
              </button>
              <button 
                type="button" 
                className="btn btn-outline-secondary" 
                onClick={resetFilters}
              >
                Reset
              </button>
            </div>
          </form>
        </div>
      </div>

      {loading ? (
        <div className="text-center my-5">
          <div className="spinner-border text-primary" role="status">
            <span className="visually-hidden">Loading...</span>
          </div>
          <p>Loading stores...</p>
        </div>
      ) : (
        <SortableTable 
          columns={columns} 
          data={stores} 
          className="table-striped table-hover"
        />
      )}

      {/* Rating Modal */}
      {showRatingModal && selectedStore && (
        <div className="modal-overlay show d-block">
          <div className="modal-dialog modal-dialog-centered">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {selectedStore.userRating ? "Update Rating" : "Rate Store"}
                </h5>
                <button 
                  type="button" 
                  className="btn-close" 
                  onClick={() => setShowRatingModal(false)}
                  disabled={ratingLoading}
                ></button>
              </div>

              <form onSubmit={submitRating}>
                <div className="modal-body">
                  <div className="mb-3">
                    <label className="form-label">Store:</label>
                    <p className="fw-bold">{selectedStore.name}</p>
                  </div>

                  <div className="mb-3">
                    <label className="form-label">Your Rating (1-5)</label>
                    <div className="rating-stars d-flex gap-1">
                      {[1, 2, 3, 4, 5].map((value) => (
                        <button
                          key={value}
                          type="button"
                          className={`btn btn-star ${value <= rating ? "active" : ""}`}
                          onClick={() => setRating(value)}
                          disabled={ratingLoading}
                        >
                          ★
                        </button>
                      ))}
                    </div>
                    <div className="mt-1">
                      <small className="text-muted">
                        Current selection: {rating} star{rating !== 1 ? 's' : ''}
                      </small>
                    </div>
                  </div>
                </div>

                <div className="modal-footer">
                  <button 
                    type="button" 
                    className="btn btn-secondary" 
                    onClick={() => setShowRatingModal(false)}
                    disabled={ratingLoading}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="btn btn-primary" 
                    disabled={ratingLoading}
                  >
                    {ratingLoading ? (
                      <>
                        <span className="spinner-border spinner-border-sm me-2" role="status" aria-hidden="true"></span>
                        Submitting...
                      </>
                    ) : (
                      "Submit Rating"
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        .modal-overlay {
          position: fixed;
          top: 0;
          left: 0;
          right: 0;
          bottom: 0;
          background-color: rgba(0,0,0,0.5);
