"use client"

import { useEffect, useState } from "react"
import SortableTable from "../../components/SortableTable"
import api from "../../services/api"

const AdminUsers = () => {
  const [users, setUsers] = useState([])
  const [loading, setLoading] = useState(true)
  const [error, setError] = useState("")
  const [filters, setFilters] = useState({
    name: "",
    email: "",
    address: "",
    role: "",
  })
  const [showAddUserModal, setShowAddUserModal] = useState(false)
  const [newUser, setNewUser] = useState({
    name: "",
    email: "",
    address: "",
    password: "",
    role: "user",
  })

  useEffect(() => {
    fetchUsers()
  }, [])

  const fetchUsers = async () => {
    try {
      setLoading(true)
      const response = await api.get("/admin/users", { params: filters })

      // Transform the data to ensure storeRating is properly handled
      const transformedUsers = response.data.map(user => ({
        ...user,
        storeRating: user.storeRating ? parseFloat(user.storeRating) : null
      }))

      setUsers(transformedUsers)
      setError("")
    } catch (err) {
      setError("Failed to load users")
      console.error(err)
    } finally {
      setLoading(false)
    }
  }

  const handleFilterChange = (e) => {
    const { name, value } = e.target
    setFilters({
      ...filters,
      [name]: value,
    })
  }

  const applyFilters = (e) => {
    e.preventDefault()
    fetchUsers()
  }

  const resetFilters = () => {
    setFilters({
      name: "",
      email: "",
      address: "",
      role: "",
    })
  }

  const handleNewUserChange = (e) => {
    const { name, value } = e.target
    setNewUser({
      ...newUser,
      [name]: value,
    })
  }

  const addUser = async (e) => {
    e.preventDefault()
    try {
      await api.post("/admin/users", newUser)
      setShowAddUserModal(false)
      setNewUser({
        name: "",
        email: "",
        address: "",
        password: "",
        role: "user",
      })
      fetchUsers()
    } catch (err) {
      setError(err.response?.data?.message || "Failed to add user")
    }
  }

  const columns = [
    { key: "name", label: "Name", sortable: true },
    { key: "email", label: "Email", sortable: true },
    { key: "address", label: "Address", sortable: true },
    { key: "role", label: "Role", sortable: true },
    {
      key: "storeRating",
      label: "Store Rating",
      sortable: true,
      render: (row) => {
        // Safely handle store rating display
        if (row.role !== "store_owner") return "N/A"
        const rating = parseFloat(row.storeRating)
        return !isNaN(rating) ? rating.toFixed(1) : "No ratings"
      },
    },
    {
      key: "actions",
      label: "Actions",
      sortable: false,
      render: (row) => (
        <div className="action-buttons">
          <button
            className="btn btn-sm btn-edit"
            onClick={(e) => {
              e.stopPropagation()
              // Handle edit user
            }}
          >
            Edit
          </button>
          <button
            className="btn btn-sm btn-delete"
            onClick={(e) => {
              e.stopPropagation()
              // Handle delete user
            }}
          >
            Delete
          </button>
        </div>
      ),
    },
  ]

  return (
    <div className="container">
      <h1>Manage Users</h1>
      {error && <div className="error-message">{error}</div>}

      <div className="actions-bar">
        <button className="btn btn-primary" onClick={() => setShowAddUserModal(true)}>
          Add New User
        </button>
      </div>

      <div className="filters-container">
        <h3>Filters</h3>
        <form onSubmit={applyFilters}>
          <div className="filters-grid">
            <div className="form-group">
              <label htmlFor="name">Name</label>
              <input type="text" id="name" name="name" value={filters.name} onChange={handleFilterChange} />
            </div>

            <div className="form-group">
              <label htmlFor="email">Email</label>
              <input type="text" id="email" name="email" value={filters.email} onChange={handleFilterChange} />
            </div>

            <div className="form-group">
              <label htmlFor="address">Address</label>
              <input type="text" id="address" name="address" value={filters.address} onChange={handleFilterChange} />
            </div>

            <div className="form-group">
              <label htmlFor="role">Role</label>
              <select id="role" name="role" value={filters.role} onChange={handleFilterChange}>
                <option value="">All Roles</option>
                <option value="admin">Admin</option>
                <option value="user">User</option>
                <option value="store_owner">Store Owner</option>
              </select>
            </div>
          </div>

          <div className="filters-actions">
            <button type="submit" className="btn btn-primary">
              Apply Filters
            </button>
            <button type="button" className="btn btn-secondary" onClick={resetFilters}>
              Reset
            </button>
          </div>
        </form>
      </div>

      {loading ? (
        <div className="loading">Loading users...</div>
      ) : (
        <SortableTable columns={columns} data={users} />
      )}

      {showAddUserModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h2>Add New User</h2>
              <button className="modal-close" onClick={() => setShowAddUserModal(false)}>
                &times;
              </button>
            </div>

            <form onSubmit={addUser}>
              <div className="form-group">
                <label htmlFor="userName">Name</label>
                <input
                  type="text"
                  id="userName"
                  name="name"
                  value={newUser.name}
                  onChange={handleNewUserChange}
                  required
                  minLength={20}
                  maxLength={60}
                />
                <small>Name must be between 20 and 60 characters</small>
              </div>

              <div className="form-group">
                <label htmlFor="userEmail">Email</label>
                <input
                  type="email"
                  id="userEmail"
                  name="email"
                  value={newUser.email}
                  onChange={handleNewUserChange}
                  required
                />
              </div>

              <div className="form-group">
                <label htmlFor="userAddress">Address</label>
                <textarea
                  id="userAddress"
                  name="address"
                  value={newUser.address}
                  onChange={handleNewUserChange}
                  required
                  maxLength={400}
                />
                <small>Address must be less than 400 characters</small>
              </div>

              <div className="form-group">
                <label htmlFor="userPassword">Password</label>
                <input
                  type="password"
                  id="userPassword"
                  name="password"
                  value={newUser.password}
                  onChange={handleNewUserChange}
                  required
                  minLength={8}
                  maxLength={16}
                />
                <small>
                  Password must be 8-16 characters with at least one uppercase letter and one special character
                </small>
              </div>

              <div className="form-group">
                <label htmlFor="userRole">Role</label>
                <select id="userRole" name="role" value={newUser.role} onChange={handleNewUserChange} required>
                  <option value="admin">Admin</option>
                  <option value="user">User</option>
                  <option value="store_owner">Store Owner</option>
                </select>
              </div>

              <div className="modal-actions">
                <button type="submit" className="btn btn-primary">
                  Add User
                </button>
                <button type="button" className="btn btn-secondary" onClick={() => setShowAddUserModal(false)}>
                  Cancel
                </button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  )
}

export default AdminUsers
